import React, { Component } from "react";
import { Card, CardHeader, CardImg, CardText, CardTitle } from "reactstrap";

class DishDetail extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    const dishDetailComponent = (
      <div className="col-12 col-md-5 m-1" key={this.props.dishDetail.id}>
        <Card>
          <CardImg
            width="100%"
            src={this.props.dishDetail.image}
            alt={this.props.dishDetail.name}
          />
          <CardTitle className="text-left ml-2 font-weight-bold">
            {this.props.dishDetail.name}
          </CardTitle>
          <CardText>{this.props.dishDetail.description}</CardText>
        </Card>
      </div>
    );

    const showDishComments = (
      <div className="col-12 col-md-5 m-1">
        <Card>
          <CardHeader className="font-weight-bold text-left ml-2">
            Comments
          </CardHeader>
          {dishComments}
        </Card>
      </div>
    );

    const dishComments = this.props.dishDetail.comments.map((comment) => {
      <CardText>{comment.comment}</CardText>;
      <CardText>
        -- {comment.author}, {comment.date}
      </CardText>;
    });

    return <div className="row">{dishDetailComponent}</div>;
  }
}

export default DishDetail;
